var React = require('react');
var stateRouter = require('app/router');

module.exports = React.createClass({
  render: function() {
    var {...props} = this.props;
    var sref = this.props['ui-sref'];
    if (!sref) { throw new Error('Link is missing required attribute value: ui-sref'); }
    var href = stateRouter.makePath(sref);
    //if (!href) { throw new Error(`State ${sref} does not exist`); } // asr does this already
    props.href = href;
    return React.createElement('a', props);
  }
});
