
module.exports = {
  ready: deviceReady,
  is: isDevice
};

function deviceReady() {
  return new Promise(function(resolve, reject) {
    if (window.cordova) {
      document.addEventListener("deviceready", function() {
        resolve();
      });
    } else {
      resolve();
    }
  });
}

function isDevice(device) {
  return window.device && window.device.platform === device;
}
