var exec = require('child_process').exec;

module.exports = function(infile, cb) {
  var parts = infile.split('.');
  parts.pop();
  parts.push('mp4');
  var outfile = parts.join('.');
  var cmd = ['avconv', '-i', infile, '-strict experimental', '-movflags faststart', outfile].join(' ');
  exec(cmd, cb);
};
