var bcmgr = require('app/broadcasts/manager');

module.exports = function(app) {
  app.get('/broadcasts', function(req, res) {
    var broadcasts = bcmgr.getLiveBroadcasts();
    var result = Object.keys(broadcasts).map(key => {
      var broadcast = broadcasts[key];
      return {
        id: broadcast.id
      }
    });
    res.json(result);
  });
}
